define(['require', 'app'], function(require, APP) {

    /**
     * Controller for Screen2 generated by Appery.io
     * @module Screen2
     */

    APP.controller('Screen2', ['$scope', 'Apperyio', controller]);

    function controller($scope, Apperyio) {

        /**
         * user controller variables
         */
        $scope.taskDescriptionVar = Apperyio.EntityAPI('String');
        $scope.anxiousChecked = Apperyio.EntityAPI('Boolean');
        $scope.angryChecked = Apperyio.EntityAPI('Boolean');
        $scope.sadChecked = Apperyio.EntityAPI('Boolean');
        $scope.guiltyChecked = Apperyio.EntityAPI('Boolean');
        $scope.disgustedChecked = Apperyio.EntityAPI('Boolean');
        $scope.debugEnabled = Apperyio.EntityAPI('Boolean');

        /**
         * User controller functions
         */
        /**
         * @function init
         */
        $scope.init = function() {
            //Enabling debug 
            $scope.debugEnabled = false;
            //On load screen logic
            var $routeParams = Apperyio.get("$routeParams");
            //console.log($routeParams.a1);
            $scope.taskDescriptionVar = $routeParams.a1;
        };

        /**
         * @function button1Click
         */
        $scope.button1Click = function() {
            if ($scope.debugEnabled) {
                console.log($scope.anxiousChecked);
                console.log($scope.angryChecked);
                console.log($scope.sadChecked);
                console.log($scope.guiltyChecked);
                console.log($scope.disgustedChecked);
            }
            //console.log($scope.taskDescriptionVar);
            Apperyio.navigateTo("Screen3", {
                a1: $scope.taskDescriptionVar,
                a2: $scope.anxiousChecked,
                a3: $scope.angryChecked,
                a4: $scope.sadChecked,
                a5: $scope.guiltyChecked,
                a6: $scope.disgustedChecked
            });

        };

    }

});