define(['require', 'app'], function(require, APP) {

    /**
     * Controller for Screen1 generated by Appery.io
     * @module Screen1
     */

    APP.controller('Screen1', ['$scope', 'Apperyio', controller]);

    function controller($scope, Apperyio) {

        /**
         * user controller variables
         */
        $scope.taskDescriptionVar = Apperyio.EntityAPI('String');

        /**
         * User controller functions
         */
        /**
         * @function init
         */
        $scope.init = function() {
            //On load screen logic
        };

        /**
         * @function goToPage2
         */
        $scope.goToPage2 = function() {
            Apperyio.navigateTo("Screen2", {
                a1: $scope.taskDescriptionVar
            });

        };

        /**
         * @function goToPage6
         */
        $scope.goToPage6 = function() {
            Apperyio.navigateTo("Screen6", {
                a1: $scope.taskDescriptionVar
            });

        };

    }

});